import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

export default function DisasterWhispererUI() {
  const [query, setQuery] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const [analysisDone, setAnalysisDone] = useState(false);
  const [response, setResponse] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  useEffect(() => {
    if (submitted) {
      const timer = setTimeout(() => {
        setAnalysisDone(true);
        setResponse("Based on the current terrain and rainfall data, a landslide is likely in Mandi District within the next 36 hours.");
        setChatHistory((prev) => [
          ...prev,
          { question: query, answer: "Based on the current terrain and rainfall data, a landslide is likely in Mandi District within the next 36 hours." }
        ]);
      }, 2500);
      return () => clearTimeout(timer);
    }
  }, [submitted]);

  const handleQuerySubmit = () => {
    if (query.trim()) {
      setSubmitted(true);
    }
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center bg-no-repeat text-white font-sans"
      style={{ backgroundImage: "url('/earth.jpg')" }}
    >
      <div className="absolute inset-0 bg-black/60 backdrop-blur-md z-0" />

      {/* Title */}
      <div className="relative z-10 text-center pt-10">
        <h1 className="text-5xl font-extrabold text-orange-400 drop-shadow-xl">Disaster Whisperer</h1>
        <p className="text-white text-xl mt-2">Predicting the next spatial risk before it strikes</p>
      </div>

      {/* Main Screen */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
        className="flex flex-col md:flex-row px-4 md:px-8 pt-20 gap-6 z-10 relative"
      >
        {/* Left Panel */}
        <div className="w-full md:w-1/2 space-y-4">
          {!submitted && (
            <div className="bg-white/10 backdrop-blur-md p-4 rounded-xl shadow-xl flex items-center gap-4">
              <input
                type="text"
                placeholder="Type your query..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="flex-1 bg-transparent border border-white/30 p-2 rounded text-white"
              />
              <button
                onClick={handleQuerySubmit}
                className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded shadow"
              >
                Send
              </button>
            </div>
          )}

          {submitted && !analysisDone && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="bg-black/50 p-6 rounded-xl"
            >
              <p className="text-orange-300 text-lg animate-pulse">Running terrain and rainfall analysis‚Ä¶</p>
            </motion.div>
          )}

          {analysisDone && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.2 }}
              className="bg-black/50 p-6 rounded-xl space-y-4"
            >
              <h3 className="text-xl font-bold text-orange-400">Predicted Disaster: Landslide</h3>
              <p>üìç <span className="text-white">Location: Mandi District, Himachal Pradesh</span></p>
              <p>üéØ <span className="text-white">Probability: 84%</span></p>
              <p>‚è∞ <span className="text-white">Estimated Time: Next 36 Hours</span></p>

              <div className="h-40 bg-white/10 rounded-xl flex items-center justify-center text-sm text-white">
                [Rainfall vs Soil Saturation Graph]
              </div>

              <div className="w-full bg-orange-800/40 rounded-full h-2">
                <div className="bg-orange-500 h-2 rounded-full w-[84%]" />
              </div>
            </motion.div>
          )}
        </div>

        {/* Right Panel: Map + Chat Panel */}
        <div className="w-full md:w-1/2 space-y-4">
          <div className="bg-black/50 p-4 rounded-xl relative h-[300px] flex flex-col justify-center items-center text-white text-sm">
            <div>[Interactive Map Zooming to Himachal Pradesh]</div>
            {analysisDone && (
              <motion.div
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ delay: 0.5 }}
                className="text-center mt-4"
              >
                <div className="w-10 h-10 bg-red-600 rounded-full animate-bounce mx-auto" />
                <p className="text-orange-300 mt-1">High Risk Zone</p>
              </motion.div>
            )}
          </div>

          {/* Right Panel Chat UI */}
          <div className="bg-white/10 backdrop-blur-md p-4 rounded-xl text-white min-h-[220px] flex flex-col justify-between">
            <div className="overflow-y-auto max-h-40 space-y-2">
              {chatHistory.map((chat, index) => (
                <div key={index} className="bg-white/5 p-2 rounded text-sm">
                  <p className="text-orange-300 font-semibold">You:</p>
                  <p className="text-white">{chat.question}</p>
                  <p className="text-orange-300 font-semibold mt-1">AI:</p>
                  <p className="text-white">{chat.answer}</p>
                </div>
              ))}
            </div>

            <div className="mt-4 flex gap-2">
              <input
                type="text"
                placeholder="Ask about spatial risk..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="flex-1 bg-transparent border border-white/30 p-2 rounded text-white"
              />
              <button
                onClick={handleQuerySubmit}
                className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded shadow"
              >
                Ask
              </button>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
}
